tooltip.patternFunction:
var deviceType = data.Tipo;

function buildRow(label, value) {
    return `
        <div style="display:flex;flex-direction:row;align-items:baseline;margin-bottom:9px;">
            <div style="font-size:13px;line-height:16px;font-weight:500;color:rgba(0, 0, 0, 0.38);width:120px">${label}</div>
            <div style="font-size:14px;line-height:20px;font-weight:500;letter-spacing:0.25px;color:#29313C">${value}</div>
        </div>
    `;
}

if (typeof deviceType !== 'undefined') {
    let content = `
    <div style="display:flex;flex-direction:column;margin-bottom:8px;font-family:'Roboto';font-weight:500;font-size:16px;line-height:24px;letter-spacing:0.25px;color:#29313C">
        <div>${data.entityLabel ?? 'Sin nombre'}</div>
        <div style="width: 100%;border-bottom:1px solid rgba(0, 0, 0, 0.12);margin:12px 0;"></div>
    `;

    if (deviceType === "lanza") {
        content +=
            buildRow("Temperatura", `${data.temperature?.toFixed(1) ?? 'N/A'} °C`) +
            buildRow("Humedad", `${data.humidity?.toFixed(0) ?? 'N/A'} %`);
    } else if (deviceType === "estMeteo") {
        content +=
            buildRow("Temperatura", `${data.temperature?.toFixed(1) ?? 'N/A'} °C`) +
            buildRow("Humedad", `${data.humidity?.toFixed(0) ?? 'N/A'} %`) +
            buildRow("Viento", `${data.windSpeed?.toFixed(1) ?? 'N/A'} m/s`) +
            buildRow("Dirección", `${data.windDir?.toFixed(0) ?? 'N/A'}°`) +
            buildRow("Lluvia", `${data.rain?.toFixed(1) ?? 'N/A'} mm`) +
            buildRow("Presión", `${data.pressure?.toFixed(1) ?? 'N/A'} hPa`);
    } else if (deviceType === "pozo") {
        const estadoPozo = data.pozo == 1 ? "Con Agua" : "Seco";
        content +=
            buildRow("Nivel de Aguada", `${data.nivelAguada?.toFixed(1) ?? 'N/A'} %`) +
            buildRow("Nivel de Tanque", `${data.nivelTanque?.toFixed(1) ?? 'N/A'} %`) +
            buildRow("Pozo", estadoPozo);
    } else if (deviceType === "Ultrasonico") {
        const estadoPozo = data.pozo == 1 ? "Con Agua" : "Seco";
        content +=
            buildRow("Nivel de Tanque", `${data.waterLevelPerc?.toFixed(1) ?? 'N/A'} %`) +
            buildRow("Volumen de Tanque", `${data.waterVolume?.toFixed(0) ?? 'N/A'} L`) +
            buildRow("Temperatura", `${data.data_temperature_2?.toFixed(1) ?? 'N/A'} °C`)
    } else if (deviceType === "Freatimetro") {
        content +=
            buildRow("Nivel Napa", `${data.nivelNapa?.toFixed(1) ?? 'N/A'} m`);
    } else if (deviceType === "comedero") {
        content +=
            buildRow("Comedero 1", `${data.comedero1?.toFixed(0) ?? 'N/A'} %`) +
            buildRow("Comedero 2", `${data.comedero2?.toFixed(0) ?? 'N/A'} %`) +
            buildRow("Comedero 3", `${data.comedero3?.toFixed(0) ?? 'N/A'} %`);
    } else {
        content += buildRow("Tipo", "Desconocido");
    }

    content += `
    <div style="text-align:center; margin-top:16px;">
        <link-act name="navigate_to_details">Detalles</link-act>
    </div>
</div>`;



    return content;
}

return `<b>${data.entityName ?? 'Dispositivo'}</b><br/>Tipo desconocido<br/>`;

