actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
openReportDialog();

function openReportDialog() {
  customDialog.customDialog(htmlTemplate, ReportDialogController).subscribe();
}

/* ============ Config ============ */
const BUCKET_MS = 60_000;   // agrupar por minuto
const KEY_BATCH_SIZE = 10;  // cuántas keys por request (evita URLs gigantes)
const PER_KEY_LIMIT = 200_000; // puntos máximos por key (sobra para 1 año a 1/h)

/* ============ Auth helpers ============ */
function readLocalJwt() {
  try {
    for (const k of ['jwt_token','tb.auth_token','token']) {
      const v = localStorage.getItem(k);
      if (v && typeof v === 'string') return v.replace(/^Bearer\s+/i,'');
    }
  } catch(_) {}
  return null;
}
function isJwtExpired(jwt) {
  try {
    const p = JSON.parse(atob(jwt.split('.')[1].replace(/-/g,'+').replace(/_/g,'/')));
    if (!p?.exp) return false;
    return Math.floor(Date.now()/1000) >= p.exp;
  } catch(_) { return false; }
}
async function refreshJwtIfPossible() {
  try {
    const res = await fetch('/api/auth/token', { method:'POST', credentials:'include' });
    if (!res.ok) return null;
    const data = await res.json();
    const t = data?.token || data?.jwtToken || null;
    if (t) { try { localStorage.setItem('jwt_token', t); } catch(_) {} }
    return t;
  } catch(_) { return null; }
}
async function getJwtToken() {
  let t = readLocalJwt();
  if (t && !isJwtExpired(t)) return t;
  const r = await refreshJwtIfPossible();
  return r || t || null;
}
async function fetchWithAuth(url, options = {}) {
  let jwt = await getJwtToken();
  const headers = new Headers(options.headers || {});
  if (jwt) headers.set('X-Authorization', 'Bearer ' + jwt);
  headers.set('accept', headers.get('accept') || 'application/json');

  let res = await fetch(url, { ...options, headers });
  if (res.status === 401) {
    const newJwt = await refreshJwtIfPossible();
    if (newJwt) {
      headers.set('X-Authorization', 'Bearer ' + newJwt);
      res = await fetch(url, { ...options, headers });
    }
  }
  console.log(jwt)
  console.log(res)
  return res;
}

/* ============ Device helpers ============ */
function getDeviceId() {
  try {
    if (typeof entityId !== 'undefined' && entityId?.id) return entityId.id;
    const a = widgetContext?.ctx?.stateController?.getEntityId?.();
    if (a?.id) return a.id;
    const b = widgetContext?.ctx?.defaultSubscription?.targetEntity?.id?.id;
    if (b) return b;
    const c = widgetContext?.ctx?.datasources?.[0]?.entity?.id?.id;
    if (c) return c;
  } catch(_) {}
  return null;
}
async function getDeviceLabelOrName(deviceId) {
  try {
    const res = await fetchWithAuth(`/api/device/${deviceId}`);
    if (!res.ok) return 'dispositivo';
    const dev = await res.json();
    return (dev?.label && String(dev.label).trim()) || dev?.name || 'dispositivo';
  } catch(_) { return 'dispositivo'; }
}

/* ============ Fechas ============ */
function getDateString(v) {
  if (typeof v === 'string') return v;
  if (v instanceof Date) return v.toISOString().slice(0,10);
  return '';
}
function normalizeDateString(input) {
  if (!input) return '';
  if (/^\d{4}-\d{2}-\d{2}$/.test(input)) return input; // YYYY-MM-DD
  const m = String(input).match(/^(\d{2})\/(\d{2})\/(\d{4})$/); // DD/MM/YYYY
  if (m) return `${m[3]}-${m[2]}-${m[1]}`;
  const d = new Date(input);
  return isNaN(d) ? '' : d.toISOString().slice(0,10);
}
function toDDMMYYYY(iso) { // 'YYYY-MM-DD' -> 'dd-mm-aaaa'
  const [y,m,d] = iso.split('-');
  return `${d}-${m}-${y}`;
}
function pad2(n){ return String(n).padStart(2,'0'); }
function fmtIsoLocalMinute(ts) { // 'YYYY-MM-DD HH:mm'
  const d = new Date(ts);
  return `${d.getFullYear()}-${pad2(d.getMonth()+1)}-${pad2(d.getDate())} `
       + `${pad2(d.getHours())}:${pad2(d.getMinutes())}`;
}

/* ============ visibleKeys SOLO SERVER_SCOPE ============ */
async function getServerVisibleKeys(deviceId) {
  const url = `/api/plugins/telemetry/DEVICE/${deviceId}/values/attributes?serverKeys=visibleKeys`;
  const res = await fetchWithAuth(url);
  if (!res.ok) throw new Error('No se pudo leer visibleKeys (SERVER_SCOPE)');
  const data = await res.json();

  let items = [];
  if (Array.isArray(data)) items = data;
  else if (Array.isArray(data.server)) items = data.server;

  const it = items.find(x => x?.key === 'visibleKeys' && x?.value);
  if (!it) throw new Error('visibleKeys no existe en SERVER_SCOPE');
  let vk = typeof it.value === 'string' ? JSON.parse(it.value) : it.value;
  if (!vk || typeof vk !== 'object' || !Object.keys(vk).length) {
    throw new Error('visibleKeys (SERVER_SCOPE) está vacío');
  }

  const keys = Object.keys(vk);
  const meta = {};
  keys.forEach(k => {
    const o = vk[k] || {};
    meta[k] = { label: o.label || k, unit: o.unit || '' };
  });
  return { keys, meta };
}

/* ============ Fetch único por key (o lote), SIN interval ni agg ============ */
/**
 * Devuelve series crudas dentro del rango, en lotes de keys, con limit alto.
 * { key1:[{ts,value},...], key2:[...], ... }
 */
async function fetchTimeseriesRaw(deviceId, keys, startTs, endTs, keyBatchSize = KEY_BATCH_SIZE, perKeyLimit = PER_KEY_LIMIT) {
  const out = {};
  keys.forEach(k => out[k] = []);

  for (let i = 0; i < keys.length; i += keyBatchSize) {
    const group = keys.slice(i, i + keyBatchSize);

    const url = `/api/plugins/telemetry/DEVICE/${deviceId}/values/timeseries`
      + `?keys=${encodeURIComponent(group.join(','))}`
      + `&startTs=${startTs}&endTs=${endTs}`
      + `&limit=${perKeyLimit}`   // trae TODO en un solo tiro por key
      + `&useStrictDataTypes=false`;

    const res = await fetchWithAuth(url);
   
    if (!res.ok) {
      const txt = await res.text();
      throw new Error(txt || `Error ${res.status} en Telemetry`);
    }
    const chunk = await res.json();

    for (const k of group) {
      const arr = Array.isArray(chunk?.[k]) ? chunk[k] : [];
      if (arr.length) out[k] = out[k].concat(arr);
    }
  }

  // ordenar por ts
  for (const k of keys) out[k].sort((a,b)=>a.ts - b.ts);
  return out;
}

/* ============ Bucket por minuto y CSV ============ */
function bucketTsMin(ts) {
  return Math.floor(ts / BUCKET_MS) * BUCKET_MS;
}

function buildCsvBucketed(seriesByKey, keys, meta) {
  const headers = [
    'fecha',
    'hora',
    'fecha hora', // YYYY-MM-DD HH:mm
    ...keys.map(k => {
      const {label, unit} = meta[k] || {label:k, unit:''};
      return unit ? `${label} (${unit})` : label;
    })
  ];

  // mapear último valor por minuto
  const maps = {};
  const bucketSet = new Set();

  for (const k of keys) {
    const m = new Map();
    for (const p of (seriesByKey[k] || [])) {
      const b = bucketTsMin(p.ts);
      m.set(b, p.value);      // queda el último del minuto
      bucketSet.add(b);
    }
    maps[k] = m;
  }

  const buckets = Array.from(bucketSet).sort((a,b)=>a-b);

  let csv = headers.join(',') + '\n';
  for (const b of buckets) {
    const d = new Date(b);
    const fecha = d.toLocaleDateString();
    const hora  = `${pad2(d.getHours())}:${pad2(d.getMinutes())}`; // estable para gráficos
    const fh    = fmtIsoLocalMinute(b);
    const row = [
      fecha,
      hora,
      fh,
      ...keys.map(k => maps[k].has(b) ? maps[k].get(b) : '')
    ];
    csv += row.join(',') + '\n';
  }
  return csv;
}

/* ============ Dialog Controller ============ */
function ReportDialogController(instance) {
  const vm = instance;

  // Loading reactivo y flags de UI
  vm.loading$ = new widgetContext.rxjs.BehaviorSubject(false);
  vm.status = '';
  vm.success = false;

  vm.reportFormGroup = vm.fb.group({
    startDate: [null, vm.validators.required],
    endDate:   [null, vm.validators.required]
  });

  vm.cancel = () => vm.dialogRef.close(null);

  vm.generateReport = async function () {
    vm.status = '';
    vm.success = false;
    vm.loading$.next(true);

    try {
      if (vm.reportFormGroup.invalid) {
        vm.status = 'Selecciona ambas fechas.';
        return;
      }

      // === Fechas ===
      const startRaw  = vm.reportFormGroup.get('startDate').value;
      const endRaw    = vm.reportFormGroup.get('endDate').value;
      const startDate = getDateString(startRaw) || normalizeDateString(String(startRaw||''));
      const endDate   = getDateString(endRaw)   || normalizeDateString(String(endRaw||''));
      if (!startDate || !endDate) {
        vm.status = 'Selecciona ambas fechas.';
        return;
      }
      const startTs = new Date(`${startDate}T00:00:00`).getTime();
      const endTs   = new Date(`${endDate}T23:59:59`).getTime();
      if (isNaN(startTs) || isNaN(endTs)) {
        vm.status = 'Formato de fecha inválido.';
        return;
      }

      // === Device & visibleKeys (SERVER_SCOPE) ===
      const deviceId    = getDeviceId();
      if (!deviceId) { vm.status = 'No se pudo resolver el Device ID.'; return; }
      const deviceLabel = await getDeviceLabelOrName(deviceId);
      const { keys, meta } = await getServerVisibleKeys(deviceId);

      // === Telemetría cruda en lotes (rápido) ===
      const seriesByKey = await fetchTimeseriesRaw(deviceId, keys, startTs, endTs, KEY_BATCH_SIZE, PER_KEY_LIMIT);
      const hasAny = keys.some(k => (seriesByKey[k]||[]).length > 0);
      if (!hasAny) { vm.status = 'No hay datos en ese rango.'; return; }

      // === CSV bucketizado por minuto + columna "fecha hora" ===
      const csvCore = buildCsvBucketed(seriesByKey, keys, meta);

      // === Blob y nombre de archivo (¡definidos acá!)
      const BOM = '\uFEFF';
      const blob = new Blob([BOM + csvCore], { type: 'text/csv;charset=utf-8' });
      const prettyStart = toDDMMYYYY(startDate); // dd-mm-aaaa
      const prettyEnd   = toDDMMYYYY(endDate);
      const safeLabel   = String(deviceLabel).trim().replace(/[^\w\s\-\.]/g, '').replace(/\s+/g,'_');
      const fileName    = `${safeLabel}_reporte_${prettyStart}_a_${prettyEnd}.csv`;

      // === Apagar loading ANTES del click, mostrar éxito y refrescar UI ===
      vm.loading$.next(false);
      vm.success = true;
      instance.$scope?.$applyAsync?.();
      await Promise.resolve(); // yieldea 1 tick para que Angular pinte

      // === Disparar descarga ===
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = fileName;
      document.body.appendChild(a);
      a.click();

      // Limpieza y cierre (deja que el click salga primero)
      setTimeout(() => {
        try { URL.revokeObjectURL(url); } catch(_) {}
        try { document.body.removeChild(a); } catch(_) {}
        // cerrar el diálogo automáticamente
        vm.dialogRef.close({ ok: true, fileName });
      }, 2000);

    } catch (err) {
      vm.status = 'Error: ' + (err?.message || err);
    } finally {
      // asegurar que quede apagado si hubo early return con error
      if (vm.loading$.getValue()) vm.loading$.next(false);
      instance.$scope?.$applyAsync?.();
    }
  };
}

