elementClick.customFunction:
// Arrays for categorizing customers
var OnlyWater = ["Customer A",];
var OnlyEnergy = ["Customer B", "Customer C"];
var AllReports = ["Customer D"];

// Retrieve the customer_title from the hidden div
var customerTitle = widgetContext.$container.find('#customerTitle').text().trim();

// Find the submenu elements
var waterReport = widgetContext.$container.find('#WaterReport');
var energyReport = widgetContext.$container.find('#EnergyReport');

// Find the Reports submenu and arrow
var reportsSubmenu = widgetContext.$container.find('#reportsSubmenu');
var reportsArrow = widgetContext.$container.find('#reportsArrow');

// Toggle the visibility of the submenu directly based on the current state
if (reportsSubmenu.is(':visible')) {
    reportsSubmenu.hide();
    reportsArrow.removeClass('rotate');
} else {
    reportsSubmenu.show();
    reportsArrow.addClass('rotate');
}

// Determine which report options to show based on the customer title
if (OnlyWater.includes(customerTitle)) {
    waterReport.show();
    energyReport.hide();
} else if (OnlyEnergy.includes(customerTitle)) {
    waterReport.hide();
    energyReport.show();
} else if (AllReports.includes(customerTitle)) {
    waterReport.show();
    energyReport.show();
} else {
    // Default behavior: Show all reports if the customer is not listed in any array
    waterReport.show();
    energyReport.show();
}

