dataKeys.postFuncBody:
const secs = Number(value);
if (!isFinite(secs)) {
    return '';
}
if (secs >= 3600) {
    const hs = secs / 3600;
    return hs.toFixed(2) + ' hs';
} else if (secs >= 60) {
    const mins = secs / 60;
    return mins.toFixed(0) + ' min';
} else {
    return secs.toFixed(0) + ' s';
}

