settings.markdownTextFunction:
// === Mapeo itemId → stateId (AJUSTAR A TU DASHBOARD) ===
const stateMap = {
  liveApp:     'default',
  Devices:     'devices',
  Reports:     null, // solo expande submenú
  WaterReport: 'water_report_state',
  EnergyReport:'energy_report_state',
  Automated:   'automated_reports_state',
  Alerts:      'alarms',
  Contact:     'contact_state'
};

// ID único para este render (evita choques si hay varios widgets)
const uid = 'sb_' + Math.random().toString(36).slice(2);

// HTML (sin <script> y sin href que cambie la URL)
const html =
  '<div id="'+uid+'" class="sidebar">' +

    '<a class="menu-item" data-item="liveApp">' +
      '<i class="fa-solid fa-map"></i> Mapa' +
    '</a>' +

    '<div class="menu-item" data-item="Devices">' +
      '<i class="fas fa-wifi"></i> Devices' +
    '</div>' +

    '<div class="menu-item" data-item="Reports" data-expand="reportsSubmenu">' +
      '<i class="fas fa-book"></i> Reports' +
      '<i class="fas fa-chevron-down" style="margin-left:auto;"></i>' +
    '</div>' +

    '<div class="submenu" id="reportsSubmenu">' +
      '<a data-item="WaterReport"><i class="fa-solid fa-droplet"></i> Water Salinity Reports</a>' +
      '<a data-item="EnergyReport"><i class="fa-solid fa-bolt"></i> Energy Reports</a>' +
    '</div>' +

    '<div class="menu-item" data-item="Automated">' +
      '<i class="fa-solid fa-file"></i> Automated Reports' +
    '</div>' +

    '<div class="menu-item" data-item="Alerts">' +
      '<i class="fas fa-bell"></i> Alerts' +
    '</div>' +

    '<div class="menu-item" data-item="Contact">' +
      '<i class="fas fa-question-circle"></i> Contact' +
    '</div>' +

  '</div>';

// Bind de eventos después de que TB inserta el HTML
setTimeout(function () {
  const root = document.getElementById(uid);
  if (!root || root.__bound) return;
  root.__bound = true;

  const submenu = root.querySelector('#reportsSubmenu');

  function clearActive() {
    root.querySelectorAll('.active').forEach(n => {
      n.classList.remove('active');
      n.setAttribute('aria-disabled', 'false');
    });
  }
  function setActive(el) {
    clearActive();
    el.classList.add('active');
    el.setAttribute('aria-disabled', 'true');
  }
  function openStateFor(itemId) {
    const stateId = stateMap[itemId];
    if (!stateId) return; // p.ej. Reports
    try {
      // usa ctx del scope de la función de valor
      if (ctx && ctx.stateController && typeof ctx.stateController.openState === 'function') {
        ctx.stateController.openState(stateId);
        return;
      }
      if (ctx && ctx.actionsApi && typeof ctx.actionsApi.openDashboard === 'function') {
        ctx.actionsApi.openDashboard(null, { stateId }, { openInDialog: false });
      }
    } catch (e) { /* silencioso */ }
  }

  // Delegación de clicks (sin cambiar la URL)
  root.addEventListener('click', function (e) {
    const item = e.target.closest('[data-item]');
    if (!item || !root.contains(item)) return;
    const id = item.getAttribute('data-item');

    // Toggle Reports (expansor)
    if (id === 'Reports') {
      e.preventDefault();
      submenu && submenu.classList.toggle('open');
      setActive(item);
      return;
    }

    e.preventDefault();
    setActive(item);
    openStateFor(id);
  });

  // Activo inicial (opcional)
  const initial = root.querySelector('[data-item="Devices"]');
  if (initial) {
    setActive(initial);
    // openStateFor('Devices'); // descomentá si querés abrir al cargar
  }
}, 0);

// devolver el HTML al widget
return html;

settings.markdownCss:
/* Mantener oculto el logo */
.logo-container { display:none !important; margin:0 !important; padding:0 !important; }

/* Base */
body {
    font-family: 'Roboto', sans-serif;
    margin: 0;
    padding: 0;
    background-color: #fff;
}

/* (Tu bloque extra de logo no es necesario si está oculto) */
/* .logo-container { ... }  — eliminado */

/* .logo { ... } — innecesario si no se muestra el logo */
/* .logo { width:auto; max-width:100%; } */

.sidebar {
    width: 100%;
    height: 100vh;
    background-color: #ffffff;
    padding: 0;
}

/* Ítems de menú */
.menu-item, .submenu a {
    display: flex;
    align-items: center;
    justify-content: flex-start;
    height: 60px;              /* 50px en submenú abajo */
    padding: 0 20px;
    width: 100%;
    color: #333;
    text-decoration: none;
    font-size: 16px;           /* 14px en submenú abajo */
    cursor: pointer;
    transition: background-color 0.3s ease, color .2s ease;
    box-sizing: border-box;
}

.menu-item:hover,
.submenu a:hover {
    background-color: rgba(235, 235, 235, 0.5); /* 50% opacity */
}

.menu-item i,
.submenu a i {
    margin-right: 10px;
}

/* Submenú */
.submenu {
    padding-left: 0;
    display: none;             /* oculto por defecto */
    user-select: none;
    flex-direction: column;
}

.submenu a {
    height: 50px;
    font-size: 14px;
    background-color: #eee;
    transition: background-color 0.3s ease, color .2s ease;
}

.submenu a:hover {
    background-color: rgba(235, 96, 96, 0.7); /* 70% opacity */
}

/* Flecha rotada cuando Reports está activo (si usás el ícono con ese id) */
#Reports:target + #reportsSubmenu + .menu-item .fa-chevron-down,
#Reports:target .fa-chevron-down {
    transform: rotate(180deg);
}

/* Abrir submenú cuando "Reports" es el :target */
#Reports:target + #reportsSubmenu {
    display: flex;
}

/* Estado ACTIVO usando :target (hash) */
.menu-item:target,
.submenu a:target {
    background: #1f6feb;
    color: #fff;
}

/* Deshabilitado visual mientras está activo */
.menu-item:target,
.submenu a:target {
    pointer-events: none;      /* no clickeable mientras activo */
    cursor: default;
}

/* Borde superior en Contact (lo mantenemos) */
#Settings{
    border-top: solid #b8bfd4 1px;
}

elementClick.customFunction:
// Arrays for categorizing customers
var OnlyWater = ["Customer A",];
var OnlyEnergy = ["Customer B", "Customer C"];
var AllReports = ["Customer D"];

// Retrieve the customer_title from the hidden div
var customerTitle = widgetContext.$container.find('#customerTitle').text().trim();

// Find the submenu elements
var waterReport = widgetContext.$container.find('#WaterReport');
var energyReport = widgetContext.$container.find('#EnergyReport');

// Find the Reports submenu and arrow
var reportsSubmenu = widgetContext.$container.find('#reportsSubmenu');
var reportsArrow = widgetContext.$container.find('#reportsArrow');

// Toggle the visibility of the submenu directly based on the current state
if (reportsSubmenu.is(':visible')) {
    reportsSubmenu.hide();
    reportsArrow.removeClass('rotate');
} else {
    reportsSubmenu.show();
    reportsArrow.addClass('rotate');
}

// Determine which report options to show based on the customer title
if (OnlyWater.includes(customerTitle)) {
    waterReport.show();
    energyReport.hide();
} else if (OnlyEnergy.includes(customerTitle)) {
    waterReport.hide();
    energyReport.show();
} else if (AllReports.includes(customerTitle)) {
    waterReport.show();
    energyReport.show();
} else {
    // Default behavior: Show all reports if the customer is not listed in any array
    waterReport.show();
    energyReport.show();
}

