CHATBOT
<!-- Raíz opcional (no se usa para incrustar, la burbuja flota) -->
<div id="n8n-chat-root"></div>

<script type="module">
  // --- CONFIG ---
  const WEBHOOK_URL = 'https://backend.nexoragro.com/webhook/67044a01-2acb-4377-9a32-8c090b67939d/chat';
  const CHAT_SESSION_KEY = 'sessionId'; // n8n espera "sessionId"

  // --- Obtener userId de ThingsBoard ---
  function getTbUserId() {
    try {
      const u = JSON.parse(localStorage.getItem('user') || 'null');
      const idFromUser = u?.userId?.id || u?.user?.id?.id;
      if (idFromUser) return idFromUser;

      const tok =
        localStorage.getItem('jwt_token') ||
        localStorage.getItem('tb.auth_token') ||
        localStorage.getItem('token');
      if (tok) {
        const base64 = (tok.split('.')[1] || '').replace(/-/g, '+').replace(/_/g, '/');
        const payload = JSON.parse(atob(base64));
        return payload.userId || payload.sub || null;
      }
    } catch (_) {}
    return null;
  }
  
  
function injectStyle(css) {
  const id = 'n8n-chat-custom-style';
  if (document.getElementById(id)) return;
  const s = document.createElement('style');
  s.id = id;
  s.textContent = css;
  document.head.appendChild(s);
}

  const sessionId = getTbUserId() || 'anon';

  // --- 1) Cargar CSS y esperar a que esté listo ---
  function loadCss(href) {
    return new Promise((resolve, reject) => {
      const id = 'n8n-chat-style';
      if (document.getElementById(id)) return resolve();
      const link = document.createElement('link');
      link.id = id;
      link.rel = 'stylesheet';
      link.href = href + '?v=' + Date.now(); // cache-busting
      link.onload = resolve;
      link.onerror = reject;
      document.head.appendChild(link);
    });
  }

  // --- 2) Importar el módulo y crear la burbuja ---
  async function boot() {
    // Asegurar estilos cargados (evita el "texto pelado")
    await loadCss('https://cdn.jsdelivr.net/npm/@n8n/chat/dist/style.css');


// Estilos personalizados (después del CSS oficial)

injectStyle(`
  :root {
    /* Colores (tu tema) */
    --chat--color-primary: #0a2342;
    --chat--color-primary-shade-50: #092037;
    --chat--color-primary-shade-100: #071a2d;
    --chat--color-secondary: #0a2342;

    /* Header compacto */
    --chat--header-height: auto;
    --chat--header--padding: 0.6rem;
    --chat--heading--font-size: clamp(1rem, 0.9rem + 0.6vw, 1.15rem);
    --chat--subtitle--font-size: clamp(0.85rem, 0.8rem + 0.2vw, 0.95rem);
    --chat--subtitle--line-height: 1.3;

    /* Tipografía de mensajes (clamp) */
    --chat--message--font-size:  10px !important;
    --chat--message-line-height: 1;

    /* Ventana y toggle (responsive) */
    --chat--window--width: min(80vw, 600px);
    --chat--window--height: min(80vh, 620px);
    --chat--toggle--size: clamp(56px, 12vw, 72px);

    /* Input más bajo */
    --chat--textarea--height: 44px;

    /* Bordes suaves */
    .n8n-chat, .n8n-chat * { font-size:1rem !important; line-height:1.35 !important; }
    --chat--border-radius: 14px;

    /* Header y burbuja usuario en azul marino */
    --chat--header--background: var(--chat--color-primary);
    --chat--header--color: #fff;
    --chat--message--user--background: var(--chat--color-primary);
    --chat--message--user--color: #fff;
  }
`);


    // Import ESM oficial
    const { createChat } = await import('https://cdn.jsdelivr.net/npm/@n8n/chat/dist/chat.bundle.es.js');

    // ¡No pasamos un contenedor de embedding! (así no ocupa fullscreen)
    createChat({
      webhookUrl: WEBHOOK_URL,
      chatSessionKey: CHAT_SESSION_KEY,
      loadPreviousSession: true,
      metadata: { sessionId },

      // UX mínima de burbuja
      position: 'bottom-right',
      launcherTitle: 'Asistente',
      initialMessages: ['Hola 👋 ¿en qué te ayudo?'],
      i18n: {
		en: {
			title: 'Asistente',
			subtitle: "",
			footer: '',
			getStarted: '',
			inputPlaceholder: 'Escribí tu consulta...',
		},
      },
      

      // Si tu versión soporta theme, descomenta:
       theme: { primary: '#111827' }
    });
  }

  boot().catch(err => {
    console.error('n8n chat error:', err);
    // Fallback visual mínimo por si CSP bloquea el CSS
    const warn = document.createElement('div');
    warn.style.cssText = 'position:fixed;bottom:12px;right:12px;padding:8px 12px;background:#111;color:#fff;border-radius:8px;font:14px/1.2 system-ui;';
    warn.textContent = 'No se pudo cargar el CSS de @n8n/chat (revisar CSP).';
    document.body.appendChild(warn);
  });
</script>

