.tb-widget-container > .tb-widget {
  border-radius: 12px !important;
  overflow: hidden;
}
.tb-powered-by-footer {
  display: none !important;
}








/* 1) Desactivar el autoscale por transform */
.tb-widget .auto-scale,
.tb-widget .tb-auto-scale,
.tb-widget .value.auto-scale,
.tb-widget .tb-value-auto-scale {
  transform: none !important;
  transform-origin: center !important;
}

/* 2) La tarjeta llena su alto y el contenido no se “achica de más” */
.tb-widget .mat-card, 
.tb-widget .tb-card,
.tb-widget .mat-card-content,
.tb-widget .tb-card-content {
  height: 100%;
}
.tb-widget .mat-card-content,
.tb-widget .tb-card-content {
  display: flex;
  align-items: center;
  justify-content: space-between; /* o center */
  gap: 12px;
}

/* 3) Tipografía fluida (no se hace minúscula) */
.tb-widget .tb-card-title,
.tb-widget .mat-card-title,
.tb-widget .title {
  font-size: clamp(14px, 1.4vw, 18px) !important;
  line-height: 1.2;
}
.tb-widget .value, 
.tb-widget .tb-card-value,
.tb-widget .latest-value {
  font-size: clamp(18px, 2.2vw, 32px) !important;
  line-height: 1.1;
  font-weight: 600;
}

/* 4) Bordes/espacios más sobrios (evita “mucho borde” al reducir) */
