// === Mapeo itemId → stateId (AJUSTAR A TU DASHBOARD) ===
const stateMap = {
  liveApp:     'default',
  Devices:     'devices',
  Reports:     null, // solo expande submenú
  WaterReport: 'water_report_state',
  EnergyReport:'energy_report_state',
  Automated:   'automated_reports_state',
  Alerts:      'alarms',
  Contact:     'contact_state'
};

// ID único para este render (evita choques si hay varios widgets)
const uid = 'sb_' + Math.random().toString(36).slice(2);

// HTML (sin <script> y sin href que cambie la URL)
const html =
  '<div id="'+uid+'" class="sidebar">' +

    '<a class="menu-item" data-item="liveApp">' +
      '<i class="fa-solid fa-map"></i> Mapa' +
    '</a>' +

    '<div class="menu-item" data-item="Devices">' +
      '<i class="fas fa-wifi"></i> Devices' +
    '</div>' +

    '<div class="menu-item" data-item="Reports" data-expand="reportsSubmenu">' +
      '<i class="fas fa-book"></i> Reports' +
      '<i class="fas fa-chevron-down" style="margin-left:auto;"></i>' +
    '</div>' +

    '<div class="submenu" id="reportsSubmenu">' +
      '<a data-item="WaterReport"><i class="fa-solid fa-droplet"></i> Water Salinity Reports</a>' +
      '<a data-item="EnergyReport"><i class="fa-solid fa-bolt"></i> Energy Reports</a>' +
    '</div>' +

    '<div class="menu-item" data-item="Automated">' +
      '<i class="fa-solid fa-file"></i> Automated Reports' +
    '</div>' +

    '<div class="menu-item" data-item="Alerts">' +
      '<i class="fas fa-bell"></i> Alerts' +
    '</div>' +

    '<div class="menu-item" data-item="Contact">' +
      '<i class="fas fa-question-circle"></i> Contact' +
    '</div>' +

  '</div>';

// Bind de eventos después de que TB inserta el HTML
setTimeout(function () {
  const root = document.getElementById(uid);
  if (!root || root.__bound) return;
  root.__bound = true;

  const submenu = root.querySelector('#reportsSubmenu');

  function clearActive() {
    root.querySelectorAll('.active').forEach(n => {
      n.classList.remove('active');
      n.setAttribute('aria-disabled', 'false');
    });
  }
  function setActive(el) {
    clearActive();
    el.classList.add('active');
    el.setAttribute('aria-disabled', 'true');
  }
  function openStateFor(itemId) {
    const stateId = stateMap[itemId];
    if (!stateId) return; // p.ej. Reports
    try {
      // usa ctx del scope de la función de valor
      if (ctx && ctx.stateController && typeof ctx.stateController.openState === 'function') {
        ctx.stateController.openState(stateId);
        return;
      }
      if (ctx && ctx.actionsApi && typeof ctx.actionsApi.openDashboard === 'function') {
        ctx.actionsApi.openDashboard(null, { stateId }, { openInDialog: false });
      }
    } catch (e) { /* silencioso */ }
  }

  // Delegación de clicks (sin cambiar la URL)
  root.addEventListener('click', function (e) {
    const item = e.target.closest('[data-item]');
    if (!item || !root.contains(item)) return;
    const id = item.getAttribute('data-item');

    // Toggle Reports (expansor)
    if (id === 'Reports') {
      e.preventDefault();
      submenu && submenu.classList.toggle('open');
      setActive(item);
      return;
    }

    e.preventDefault();
    setActive(item);
    openStateFor(id);
  });

  // Activo inicial (opcional)
  const initial = root.querySelector('[data-item="Devices"]');
  if (initial) {
    setActive(initial);
    // openStateFor('Devices'); // descomentá si querés abrir al cargar
  }
}, 0);

// devolver el HTML al widget
return html;

