tooltip.patternFunction:
var deviceType = data.Tipo;

if (typeof deviceType !== 'undefined') {
    if (deviceType === "lanza") {
        return '<b>${entityName}</b><br/>' +
            '<b>Temperatura:</b> ${temperature:1} °C<br/>' +
            '<b>Humedad:</b> ${humidity:0} %<br/>'+
            '<br/><link-act name="navigate_to_details">Detalles</link-act><br/>';
    } else if (deviceType === "estMeteo") {
        return '<b>${entityName}</b><br/>' +
            '<b>Temperatura:</b> ${temperature:1} °C<br/>' +
            '<b>Humedad:</b> ${humidity:0} %<br/>' +
            '<b>Viento:</b> ${windSpeed:1} m/s<br/>' +
            '<b>Dirección:</b> ${windDir:0}°<br/>' +
            '<b>Lluvia:</b> ${rain:1} mm<br/>'+
            '<br/><link-act name="navigate_to_details">Detalles</link-act><br/>';
    } else if (deviceType === "pozo") {
        var valorPozo = data.pozo?.[0]?.value;
        var estadoPozo = valorPozo == 1 ? "Con Agua" :
            "Seco";

        return '<b>' + data.entityName + '</b><br/>' +
            '<b>Nivel de Aguada:</b> ${nivelAguada:1} %<br/>' +
            '<b>Nivel de Tanque:</b> ${nivelTanque:1} %<br/>' +
            '<b>Pozo:</b> ' + estadoPozo + '<br/>'+
            '<br/><link-act name="navigate_to_details">Detalles</link-act><br/>';
    }
}

return '<b>${entityName}</b><br/>Tipo desconocido<br/>';

