settings.cellContentFunction:
if (value || value === 0) {
    let progress = '39, 134, 34, 1'; // verde
    let background = '234, 235, 255, 1';
    let background2 = '240, 235, 255, 1';
    let textColor = '#ffffff';

    if (value < 25) {
        progress = '209, 39, 48, 1'; // rojo
        textColor = '#000000';
    } else if (value < 70) {
        progress = '255, 213, 102, 1'; // amarillo
        textColor = '#000000';
    }

    const percent = value + '%';
    const barHeight = '28px';
    const barWidth = '60px';
    const borderRadius = '6px';

    return `
    <div style="display: flex; align-items: center; gap: 6px;">
        <div style="position: relative; display: flex; align-items: center;">
            <!-- Batería -->
            <div style="position: relative; width: ${barWidth}; height: ${barHeight}; background: rgba(${background}); border-radius: ${borderRadius}; overflow: hidden; box-shadow: inset 0 0 2px rgba(0,0,0,0.2);">
                <div style="position: absolute; top: 0; left: 0; width: ${value}%; height: 100%; background: rgba(${progress}); border-radius: ${borderRadius};"></div>
                <div style="position: absolute; width: 100%; height: 100%; display: flex; align-items: center; justify-content: center; font-weight: bold; color: ${textColor};">
                    ${parseFloat(value).toFixed(0)}%
                </div>
            </div>
             <!-- Polo positivo (pegado) -->
            <div style="width: 6px; height: 9px; background-color: rgba(${background2}); margin-left: 1px; border-radius: 2px;"></div>
        
    </div>
    `;
} else {
    return 'N/A';
}

