actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.entityId = entityId;
    vm.entityName = entityName;
    vm.attributes = {};

    vm.editEntityFormGroup = vm.fb.group({
        entityName: [''],
        attributes: vm.fb.group({
            temperatureAlarmFlag: [false],
            temperatureAlarmThreshold: [{value: null, disabled: true}],
            humidityAlarmFlag: [false],
            humidityAlarmThreshold: [{value: null, disabled: true}],
            latitude: [null],
            longitude: [null]
        })
    });

    vm.editEntityFormGroup.get('attributes').get('temperatureAlarmFlag').valueChanges
    .subscribe(activate => {
        if (activate) {
            vm.editEntityFormGroup.get('attributes').get('temperatureAlarmThreshold').enable();
        } else {
            vm.editEntityFormGroup.get('attributes').get('temperatureAlarmThreshold').disable();
        }
    });

    vm.editEntityFormGroup.get('attributes').get('humidityAlarmFlag').valueChanges
    .subscribe(activate => {
        if (activate) {
            vm.editEntityFormGroup.get('attributes').get('humidityAlarmThreshold').enable();
        } else {
            vm.editEntityFormGroup.get('attributes').get('humidityAlarmThreshold').disable();
        }
    });


    getEntityInfo();


    vm.save = function() {
        vm.editEntityFormGroup.markAsPristine();
        saveAttributes(entityId).subscribe(
            function () {
                vm.dialogRef.close(null);
            }
        );
    };

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function getEntityAttributes(attributes) {
        for (var i = 0; i < attributes.length; i++) {
            vm.attributes[attributes[i].key] = attributes[i].value;
        }
    }

    function getEntityInfo() {
        attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE').subscribe(
            function (attributes) {
                getEntityAttributes(attributes);
                vm.editEntityFormGroup.patchValue({
                    entityName: vm.entityName,
                    attributes: vm.attributes
                });
                // if(vm.attributes.temperatureAlarmFlag) {
                //     vm.editEntityFormGroup.get('attributes').get('temperatureAlarmThreshold').enable();
                // }
                // if(vm.attributes.humidityAlarmFlag) {
                //     vm.editEntityFormGroup.get('attributes').get('humidityAlarmThreshold').enable();
                // }
            }
        );
    }

    function saveAttributes(entityId) {
        let attributes = vm.editEntityFormGroup.get('attributes').value;
        let attributesArray = [];
        for (let key in attributes) {
            if (attributes[key] !== vm.attributes[key]) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }
}
