pointTooltip.patternFunction:
  // time y minTime vienen como timestamp en ms
  var endTs  = data.time;
  var startTs = data.minTime;  // asumiendo que minTime también es ts ms

  var endStr   = 'N/A';
  var startStr = 'N/A';

  if (endTs != null) {
    var endDate = new Date(Number(endTs));
    endStr = endDate.toLocaleString('es-AR', {
      timeZone: 'America/Argentina/Buenos_Aires'
    });
  }

  if (startTs != null) {
    var startDate = new Date(Number(startTs));
    startStr = startDate.toLocaleString('es-AR', {
      timeZone: 'America/Argentina/Buenos_Aires'
    });
  }

  // Latitude / Longitude pueden venir como string, los normalizamos
  var lat = data.data_latitude2 != null ? Number(data.data_latitude2) : null;
  var lon = data.data_longitude2 != null ? Number(data.data_longitude2) : null;

  var latStr = lat != null ? lat.toFixed(7) : '';
  var lonStr = lon != null ? lon.toFixed(7) : '';

  return ''
    + '<b>' + data.entityName + '</b><br/><br/>'
    + '<b>Latitude:</b> ' + latStr + '<br/>'
    + '<b>Longitude:</b> ' + lonStr + '<br/>'
    + '<b>End Time:</b> ' + endStr + '<br/>'
    + '<b>Start Time:</b> ' + startStr;

pointColor.colorFunction:
var temperature = data.rssi;
if (temperature !== undefined) {
  var minRssi = -120;
  var maxRssi = -1;
  var percent = Math.max(0, Math.min(1, (temperature - minRssi) / (maxRssi - minRssi))) * 100;
  return tinycolor.mix('red', 'blue', percent).toHexString();
}
return 'blue';
