/* Mantener oculto el logo */
.logo-container { display:none !important; margin:0 !important; padding:0 !important; }

/* Base */
body {
    font-family: 'Roboto', sans-serif;
    margin: 0;
    padding: 0;
    background-color: #fff;
}

/* (Tu bloque extra de logo no es necesario si está oculto) */
/* .logo-container { ... }  — eliminado */

/* .logo { ... } — innecesario si no se muestra el logo */
/* .logo { width:auto; max-width:100%; } */

.sidebar {
    width: 100%;
    height: 100vh;
    background-color: #ffffff;
    padding: 0;
}

/* Ítems de menú */
.menu-item, .submenu a {
    display: flex;
    align-items: center;
    justify-content: flex-start;
    height: 60px;              /* 50px en submenú abajo */
    padding: 0 20px;
    width: 100%;
    color: #333;
    text-decoration: none;
    font-size: 16px;           /* 14px en submenú abajo */
    cursor: pointer;
    transition: background-color 0.3s ease, color .2s ease;
    box-sizing: border-box;
}

.menu-item:hover,
.submenu a:hover {
    background-color: rgba(235, 235, 235, 0.5); /* 50% opacity */
}

.menu-item i,
.submenu a i {
    margin-right: 10px;
}

/* Submenú */
.submenu {
    padding-left: 0;
    display: none;             /* oculto por defecto */
    user-select: none;
    flex-direction: column;
}

.submenu a {
    height: 50px;
    font-size: 14px;
    background-color: #eee;
    transition: background-color 0.3s ease, color .2s ease;
}

.submenu a:hover {
    background-color: rgba(235, 96, 96, 0.7); /* 70% opacity */
}

/* Flecha rotada cuando Reports está activo (si usás el ícono con ese id) */
#Reports:target + #reportsSubmenu + .menu-item .fa-chevron-down,
#Reports:target .fa-chevron-down {
    transform: rotate(180deg);
}

/* Abrir submenú cuando "Reports" es el :target */
#Reports:target + #reportsSubmenu {
    display: flex;
}

/* Estado ACTIVO usando :target (hash) */
.menu-item:target,
.submenu a:target {
    background: #1f6feb;
    color: #fff;
}

/* Deshabilitado visual mientras está activo */
.menu-item:target,
.submenu a:target {
    pointer-events: none;      /* no clickeable mientras activo */
    cursor: default;
}

/* Borde superior en Contact (lo mantenemos) */
#Settings{
    border-top: solid #b8bfd4 1px;
}

