        var deviceType = device.type;
        var params = {
            entityId: entityId,
            entityName: entityName,
            entityLabel: entityLabel
        };
        var targetDashboardStateId;
        switch (deviceType) {
            case "estMeteo":
                targetDashboardStateId =
                    "estMeteo_details";
                break;
            case "pozo":
                targetDashboardStateId = "pozo_details";
                break;
            case "Ultrasonico":
                targetDashboardStateId = "pozo_details";
                break;
            case "lanza":
                targetDashboardStateId =
                "lanza_details";
                break;
            case "comedero":
                targetDashboardStateId =
                "comedero_details";
                break;
            case "Freatimetro":
                targetDashboardStateId =
                "freatimetro_details";
                break;

        }
        if (targetDashboardStateId) {
            widgetContext.stateController.updateState(
                targetDashboardStateId, params,
                false);
        }
    }
);

