settings.cardCss:
.card {
   width: 100%;
   height: 100%;
   border: 2px solid #ccc;
   box-sizing: border-box;
}

.card .content {
   padding: 20px;
   display: flex;
   flex-direction: row;
   align-items: center;
   justify-content: space-around;
   height: 100%;
   box-sizing: border-box;
}

.card .content .column {
   display: flex;
   flex-direction: column;    
   justify-content: space-around;
   height: 100%;
}

.card h1 {
    text-transform: uppercase;
    color: #999;
    font-size: 20px;
    font-weight: bold;
    margin: 0;
    padding-bottom: 10px;
    line-height: 32px;
}

.card .value {
    font-size: 38px;
    font-weight: 200;
}

.card .description {
    font-size: 20px;
    color: #999;
}

