rowClick.customFunction:
var deviceService = widgetContext.deviceService;

deviceService.getDevice(entityId.id, {ignoreLoading: true}).subscribe(
    function (device) {
        var deviceType = device.type;
        var params = {
            entityId: entityId,
            entityName: entityName
        };
        var targetDashboardStateId;
        switch (deviceType) {
          case "estMeteo":
              targetDashboardStateId = "estMeteo_details";
              break;
          case "pozo":
              targetDashboardStateId = "pozo_details";
              break;
          case "lanza":
              targetDashboardStateId = "lanza_details";
              break;

        }
        if (targetDashboardStateId) {
              widgetContext.stateController.openState(targetDashboardStateId, params, false);
        }
    }
);

