label.patternFunction:
var color;
if(data.active !== "true"){
    color = 'rgb(255, 0, 0)';
} else {
    color = 'rgb(39, 134, 34)';
}
return '<span style="border: solid ' + color + '; border-radius: 10px; color: ' + color + '; background-color: #fff; padding: 3px 5px; font-size: 14px; position: relative; bottom: 6px;">' + 
           '${entityName}' + 
        '</span>'

tooltip.patternFunction:
var area = 0;
var datas = JSON.parse(data.perimeter);
for (var i = 0; i < datas.length - 1; i++) {
    var x1 = datas[i][0];
    var y1 = datas[i][1];
    var x2 = datas[i + 1][0];
    var y2 = datas[i + 1][1];
    area += (x1 * y2 - x2 * y1);
}


return '<b>${entityName}</b><br/><br/><b>Latitude:</b> ${latitude:7}<br/><b>Longitude:</b> ${longitude:7}<br/><b>Temperature:</b> ${temperatura} 째C<br/><b>Area:</b>'+area

