// TBEL - Calculated Field: decodifica DATALOG de Dragino y emite puntos históricos
// Argumento: data (string con el payload)

var s = data;

// --- Normalizar: si viene DATALOG:"..."
var q1 = s.indexOf("\"");
var q2 = s.lastIndexOf("\"");
if (q1 >= 0 && q2 > q1) {
    s = s.substring(q1 + 1, q2);
} else {
    var p = s.indexOf(":");
    if (p >= 0 && s.substring(0, p).toUpperCase().indexOf("DATALOG") >= 0) {
        s = s.substring(p + 1).trim();
    }
}

// --- Separar en registros por "],"
var items = s.split("],");
var result = [];

// --- Aux: boolean
function toBool(v) {
    var t = ("" + v).trim().toLowerCase();
    return t == "true" || t == "1" || t == "yes" || t == "y";
}

// --- Aux: ¿string numérico entero?
function isAllDigits(t) {
    if (t.length == 0) return false;
    for (var i = 0; i < t.length; i++) {
        var c = t.charAt(i);
        if (c < '0' || c > '9') return false;
    }
    return true;
}

// --- Aux: parsear timestamp → ms
function parseTsToMs(tsRaw) {
    var t = ("" + tsRaw).trim();

    // Si es epoch numérico
    if (isAllDigits(t)) {
        var val = Long.valueOf(t);
        // Si tiene más de 10 dígitos => ya está en ms
        if (t.length > 10) {
            return val;
        } else {
            return val * 1000;
        }
    }

    // Si es "YYYY-MM-DD HH:mm:ss"
    var iso = t.replace(" ", "T") + "Z";
    var Tpos = iso.indexOf("T");
    var Zpos = iso.indexOf("Z");
    if (Tpos > 0 && Zpos > Tpos) {
        var timeOnly = iso.substring(Tpos + 1, Zpos);
        var colonCount = 0;
        for (var k = 0; k < timeOnly.length; k++)
            if (timeOnly.charAt(k) == ':') colonCount++;
        if (colonCount == 1) {
            iso = iso.substring(0, Zpos);
            iso = iso + ":00Z";
        }
    }

    var ms = new Date(iso).getTime();
    if (ms != ms) { // NaN check
        return null;
    }
    return ms;
}

// --- Procesar cada registro
var i = 0;
while (i < items.size) {
    var item = items[i].trim();

    if (item.length == 0) {
        i = i + 1;
        continue;
    }

    if (!item.startsWith("[")) {
        item = "[" + item;
    }
    if (!item.endsWith("]")) {
        item = item + "]";
    }

    var inner = item.substring(1, item.length - 1);
    var fields = inner.split(",");

    if (fields.size < 5) {
        i = i + 1;
        continue;
    }

    var hStr = fields[0].trim();
    var tStr = fields[1].trim();
    var tsStr = fields[4].trim();

    var humidity = Double.valueOf(hStr);
    var temperature = Double.valueOf(tStr);

    var tsMs = parseTsToMs(tsStr);
    if (tsMs != null) {
        result.add({
            "ts": tsMs,
            "values": {
                "data_humidity": humidity,
                "data_temperature": temperature
            }
        });
    }

    i = i + 1;
}

// --- Devolver arreglo de puntos
return result;
