// Obtenemos el campo intervalo en segundos
var finalHex = '';

if (msg.reboot != null) {
    // Mantengo tu comando de reboot igual
    finalHex = '04FF';

} else if (msg.interval != null) {
    var segundos = msg.interval;

    // Aseguramos rango válido para 3 bytes (0x000000 – 0xFFFFFF)
    if (segundos < 0) {
        segundos = 0;
    } else if (segundos > 0xFFFFFF) {
        segundos = 0xFFFFFF;
    }

    // Convertimos a HEX (sin prefijo 0x)
    var hexSeg = Integer.toString(segundos, 16);
    // Deben ser exactamente 3 bytes → 6 dígitos hex
    // Ej: 30  -> 00001E
    //     60  -> 00003C
    hexSeg = padStart(hexSeg, 6, '0').toUpperCase();

    // Prefijo fijo del comando TDC: 0x01
    // Formato final: 01 + [3 bytes de tiempo]
    // Ej: 0100001E, 0100003C
    finalHex = '01' + hexSeg;
}

// Convertimos a bytes
var bytesArr = hexToBytes(finalHex);

// A Base64
var payloadBase64 = bytesToBase64(bytesArr);

// Armamos objeto para ChirpStack
var csPayload = {
    "queueItem": {
        "data": payloadBase64,
        "fPort": 1,   // si el equipo espera otro FPort para TDC, cámbialo acá
        "id": "string",
        "isPending": true
    }
};

return {
    msg: csPayload,
    metadata: metadata,
    msgType: msgType
};

