// ==== Configurable ====
var WINDOW_MIN   = 8;     // ventana para la regresión (min)
var MAX_POINTS   = 120;   // máx puntos almacenados en buffer
var ALPHA        = 0.30;  // EMA de la tasa (0..1)
var RATE_ON      = 0.30;  // L/min para entrar a FILL/DRAIN
var RATE_OFF     = 0.15;  // L/min para volver a IDLE (histéresis)
var JUMP_DROP    = 500;   // L: salto imposible -> reset buffer
var MAX_DT_MIN   = 30;    // si pasan >30 min sin datos -> reset
// ======================

function parseJSON(s, def){ try { return JSON.parse(s); } catch(e){ return def; } }

var ts = Number(metadata.ts || Date.now());
if (typeof msg.waterVolume === 'undefined') {
  return {msg: msg, metadata: metadata, msgType: msgType};
}
var v = Number(msg.waterVolume);
if (!isFinite(v)) {
  return {msg: msg, metadata: metadata, msgType: msgType};
}

var buf = parseJSON(metadata.wv_buf || '[]', []);
var prevEma   = Number(metadata.wv_ema);
if (!isFinite(prevEma)) prevEma = null;
var prevState = String(metadata.wv_state || 'IDLE');
var segStartTs = Number(metadata.wv_seg_start_ts);
var segStartV  = Number(metadata.wv_seg_start_v);
if (!isFinite(segStartTs)) segStartTs = ts;
if (!isFinite(segStartV))  segStartV  = v;

// Reset por gap o salto imposible
if (buf.length > 0) {
  var last = buf[buf.length-1];
  var lastTs = Number(last[0]), lastV = Number(last[1]);
  if ((ts - lastTs) > MAX_DT_MIN*60000 || Math.abs(v - lastV) > JUMP_DROP) {
    buf = []; prevEma = null; prevState = 'IDLE';
    segStartTs = ts; segStartV = v;
  }
}

// Agregar muestra
buf.push([ts, v]);

// Recortar por ventana de tiempo
var cutoff = ts - WINDOW_MIN*60000;
var tmp = [];
for (var i=0;i<buf.length;i++){ if (buf[i][0] >= cutoff) tmp.push(buf[i]); }
buf = tmp;

// Limitar puntos (con llaves para Nashorn)
while (buf.length > MAX_POINTS) { buf.shift(); }

// --- Regresión lineal v(t) para pendiente en L/min ---
var n = buf.length, slopeLm = 0;
if (n >= 2) {
  var sumt=0, sumv=0;
  for (var i2=0;i2<n;i2++){ sumt += buf[i2][0]; sumv += buf[i2][1]; }
  var mt = sumt/n, mv = sumv/n;
  var s_tt=0, s_tv=0;
  for (var j=0;j<n;j++){
    var dtm = (buf[j][0]-mt)/60000.0; // minutos
    var dv  = (buf[j][1]-mv);
    s_tt += dtm*dtm; s_tv += dtm*dv;
  }
  slopeLm = s_tt>0 ? (s_tv/s_tt) : 0; // L/min
}

// EMA
var rate = (prevEma==null) ? slopeLm : (ALPHA*slopeLm + (1-ALPHA)*prevEma);

// Estado con histéresis
function nextState(prev, r){
  if (prev==='FILL')  return (r <=  RATE_OFF ? 'IDLE' : 'FILL');
  if (prev==='DRAIN') return (r >= -RATE_OFF ? 'IDLE' : 'DRAIN');
  if (r >=  RATE_ON) return 'FILL';
  if (r <= -RATE_ON) return 'DRAIN';
  return 'IDLE';
}
var state = nextState(prevState, rate);

// Cierre de tramo si cambió de estado
var segTelemetry = {};
if (state !== prevState) {
  if (prevState!=='IDLE') {
    var durMin = (ts - segStartTs)/60000.0;
    if (durMin > 0.01) {
      var dV = v - segStartV;
      segTelemetry.segmentFlag = 1;
      segTelemetry.segmentType = (prevState==='FILL'?1:-1);
      segTelemetry.segmentDurationMin = +durMin.toFixed(2);
      segTelemetry.segmentDeltaVolume = +dV.toFixed(2);
      segTelemetry.segmentRate = +(dV/durMin).toFixed(3);
    }
  }
  segStartTs = ts; segStartV = v;
}

// Salida telemetría
var stateNum = (state==='FILL')?1:((state==='DRAIN')?-1:0);
var out = {
  waterRate: +rate.toFixed(3),
  state_num: stateNum,
  waterVolume: v
};
for (var k in segTelemetry){ out[k]=segTelemetry[k]; }

// Atributos persistentes
var attrs = {
  wv_buf: JSON.stringify(buf),
  wv_ema: +rate.toFixed(6),
  wv_state: state,
  wv_seg_start_ts: segStartTs,
  wv_seg_start_v: segStartV,
  wv_last_ts: ts
};

// Empaquetado
msg = out;
metadata._attributes = JSON.stringify(attrs);
return { msg: msg, metadata: metadata, msgType: 'POST_TELEMETRY_REQUEST' };

