function toCsv(raw){
  var data = raw;
  if (typeof raw === 'string') { try { data = JSON.parse(raw); } catch(e){ data = null; } }

  // Soporta: {recipients:[{email,enabled}]}, o directamente [{email,enabled}], o array de strings
  var list = [];
  if (Array.isArray(data)) {
    list = data;
  } else if (data && Array.isArray(data.recipients)) {
    list = data.recipients;
  }

  // Normaliza a emails habilitados
  var emails = [];
  for (var i=0; i<list.length; i++){
    var r = list[i];
    var email = typeof r === 'string' ? r : (r && r.email);
    var enabled = typeof r === 'string' ? true : (r && (r.enabled===true || r.enabled==='true' || r.enabled===1 || r.enabled==='1'));
    if (enabled && email && String(email).trim()) emails.push(String(email).trim());
  }

  // dedupe (case-insensitive)
  var seen = {}, out = [];
  for (var j=0; j<emails.length; j++){
    var k = emails[j].toLowerCase();
    if (!seen[k]) { seen[k]=1; out.push(emails[j]); }
  }
  return out.join(','); // sin espacios si el nodo es sensible
}

metadata.recipientsCsv = toCsv(metadata.emailRecipients || msg.emailRecipients);
return { msg: msg, metadata: metadata, msgType: msgType };

// ALARM â†’ setear solo 2 atributos en el device
function upper(s){ return (s==null?'':String(s)).toUpperCase(); }

var status   = upper(msg.status);       // ACTIVE_UNACK, CLEARED_ACK, etc.
var severity = upper(msg.severity||''); // CRITICAL, WARNING, ...
var cleared  = (msg.cleared === true) || (String(msg.cleared) === 'true') || /^CLEARED/.test(status);

// Si estÃ¡ cleared -> "off", si no -> severidad (o INDETERMINATE de fallback)
var alarmSeverityAttr = cleared ? 'off' : (severity || 'INDETERMINATE');

// Tipo/nombre legible de la alarma
var alarmTypeAttr = msg.type || msg.name || '';

// Devolver body listo para Save Attributes
return {
  msg: {
    alarm_type: alarmTypeAttr,
    alarm_severity: alarmSeverityAttr
  },
  metadata: metadata,
  msgType: 'POST_ATTRIBUTES_REQUEST'
};
