// Script Node - Asignar visibleKeys y mainKey según tipo de dispositivo
// Se espera un msg como el que viene en DEVICE_CREATED, con msg.type

var type = msg.type;
var visibleKeys = {};
var mainKey = null;

// Normalizar tipo
if (type && typeof type === 'string') {
    type = type.trim().toLowerCase();
} else {
    type = '';
}

// Mapa de tipos → config (visibleKeys + mainKey)
var deviceTypeMap = {
    // Ultrasonico
    'ultrasonico': {
        mainKey: 'waterLevelPerc',
        visibleKeys: {
            // data_temperature_2: { label: 'Temperatura', unit: '°C' },
            waterLevelPerc: { label: 'Nivel de agua', unit: '%' },
            waterVolume:    { label: 'Volumen de agua', unit: 'L' }
        }
    },

    // Tranquera
    'tranquera': {
        mainKey: 'door_state',
        visibleKeys: {
            door_state:      { label: 'Estado', unit: '' },
            last_open_ts:    { label: 'Última apertura', unit: '' },
            open_duration_s: { label: 'Duración apertura', unit: 's' }
        }
    },

    // Lanza / riego
    'lanza': {
        mainKey: 'pressure_bar',
        visibleKeys: {
            pressure_bar: { label: 'Presión', unit: 'bar' },
            flow_l_min:   { label: 'Caudal', unit: 'L/min' }
        }
    }

    // Agregá más tipos acá:
    // 'tanque': { mainKey: '...', visibleKeys: {...} },
    // 'freatimetro': { ... }
};

// Tomar config según type
if (deviceTypeMap[type]) {
    visibleKeys = deviceTypeMap[type].visibleKeys || {};
    mainKey = deviceTypeMap[type].mainKey || null;
}

// Solo mandamos los atributos que queremos persistir
msg = {
    visibleKeys: visibleKeys,
    mainKey: mainKey
};

return {
    msg: msg,
    metadata: metadata,
    msgType: "POST_ATTRIBUTES_REQUEST"
};

var name = msg.name;

var nominal = 3.6; // valor por defecto (batería)

if (name && typeof name === 'string') {
    var trimmed = name.trim();
    if (trimmed.length > 0) {

        // Formato esperado: DS-US1-LS-0001
        // Partes: [0] = DS, [1] = US1, [2] = LS/LB/loque sea, [3] = 0001
        var parts = trimmed.split('-');
    
        if (parts.length >= 3) {
            var seg3 = parts[2]; // tercer segmento

            if (seg3 && seg3.length > 0) {
                var lastChar = seg3.charAt(seg3.length - 1).toUpperCase();

                if (lastChar === 'S') {
                    // Nodo solar
                    nominal = 4.0;
                } else if (lastChar === 'B') {
                    // Nodo a batería
                    nominal = 3.6;
                }
            }
        }
    }
}

// Para no guardar keys de más, dejamos solo la que queremos persistir
msg = { battery_nominal: nominal };

return { msg: msg, metadata: metadata, msgType: "POST_ATTRIBUTES_REQUEST" };
