var segundos = msg.interval;  // reemplazá por data.segundos si viene desde afuera

// Convertir a hex de 2 bytes (4 dígitos)
var hex = segundos.toString(16);
while (hex.length < 4) {
    hex = '0' + hex;
}
var finalHex = '01' + hex;  // Ej: 010009

// HEX a array de bytes
function hexToBytes(hexStr) {
    var result = [];
    for (var i = 0; i < hexStr.length; i += 2) {
        result.push(parseInt(hexStr.substr(i, 2), 16));
    }
    return result;
}

// Bytes a Base64 manual (ThingsBoard compatible)
function bytesToBase64(bytes) {
    var base64abc = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";
    var result = "", i;
    for (i = 0; i + 2 < bytes.length; i += 3) {
        result += base64abc[bytes[i] >> 2];
        result += base64abc[((bytes[i] & 3) << 4) | (bytes[i + 1] >> 4)];
        result += base64abc[((bytes[i + 1] & 15) << 2) | (bytes[i + 2] >> 6)];
        result += base64abc[bytes[i + 2] & 63];
    }
    if (i < bytes.length) {
        result += base64abc[bytes[i] >> 2];
        if (i + 1 < bytes.length) {
            result += base64abc[((bytes[i] & 3) << 4) | (bytes[i + 1] >> 4)];
            result += base64abc[(bytes[i + 1] & 15) << 2];
            result += "=";
        } else {
            result += base64abc[(bytes[i] & 3) << 4];
            result += "==";
        }
    }
    return result;
}

var bytes = hexToBytes(finalHex);
var base64Payload = bytesToBase64(bytes);

var msgPayload = {

  "queueItem": {
    "confirmed": false,
    "data": base64Payload,
    "fPort": 10,
    "id": "string",
    "isPending": true


  }

return {msg: msgPayload, metadata: metadata, msgType: msgType};

