// Obtenemos el campo intervalo en segundos
var finalHex = '';

    if (msg.reboot != null) {
        finalHex = 'aa5505';

    } else if (msg.interval != null) {
        var segundos = msg.interval;
       
        // Convertimos a hex de 8 dígitos (uint32), completando con ceros a la izquierda
        var hexSeg = Integer.toString(segundos, 16);
        hexSeg = padStart(hexSeg, 8,'0'); // ej: 00000015 para 21 segundos

        // Prefijo fijo del comando
        finalHex = 'AA5501'+hexSeg; // aseguro mayúsculas si querés consistencia

    }


// Convertimos a bytes
var bytesArr = hexToBytes(finalHex);

// A Base64
var payloadBase64 = bytesToBase64(bytesArr);

// Armamos objeto para ChirpStack
var csPayload = {
    "queueItem": {
        "data": payloadBase64,
        "fPort": 10,
        "id": "string",
        "isPending": true
    }
};

return {
    msg: csPayload,
    metadata: metadata,
    msgType: msgType
};

