descriptor.templateCss:
:root {
  --bg-me: #DCF8C6;            /* verde claro tipo WhatsApp */
  --fg-me: #111;
  --bg-bot: #2a2f3a;           /* gris oscuro */
  --fg-bot: #fff;
  --border: rgba(0,0,0,.12);
}

.chat-container{
  width:100%; height:100%;
  display:flex; flex-direction:column;
}

.chat-list{
  flex:1; overflow:auto; padding:12px;
  display:flex; flex-direction:column; gap:8px;
}

.chat-row{ display:flex; }
.chat-row.me{ justify-content:flex-end; }
.chat-row.bot{ justify-content:flex-start; }

.bubble{
  max-width:75%;
  padding:10px 12px; border-radius:14px;
  box-shadow: 0 1px 2px rgba(0,0,0,.08);
}
.chat-row.me .bubble{
  background:var(--bg-me); color:var(--fg-me);
  border-top-right-radius:4px;
}
.chat-row.bot .bubble{
  background:var(--bg-bot); color:var(--fg-bot);
  border-top-left-radius:4px;
}

.meta{
  display:flex; gap:8px; align-items:center;
  font-size:12px; opacity:.8; margin-bottom:4px;
}
.nick{ font-weight:600; }
.text{ white-space:pre-wrap; word-break:break-word; }

.chat-input{
  display:flex; gap:8px; align-items:flex-end;
  padding:10px; border-top:1px solid var(--border);
}
.chat-input textarea{
  flex:1; resize:none; min-height:40px; max-height:140px;
  padding:10px 12px; border-radius:10px; outline:none;
  border:1px solid var(--border); background:transparent;
  color:inherit; font:inherit;
}
.chat-input .send{
  padding:10px 14px; border-radius:10px;
  border:1px solid var(--border);
  background:rgba(0,0,0,.06); color:inherit; cursor:pointer;
}
.chat-input .send:hover{ filter:brightness(1.1); }

descriptor.controllerScript:
/****************************************************************************
 * Widget: Chat (Timeseries)
 * Lee y muestra timeseries "chat" y envía nuevos mensajes como telemetría
 * al USER actual.
 *
 * Requisitos (muy importante):
 * 1) Este widget debe ser de tipo **Timeseries**.
 * 2) Datasource: **Current user** (Usuario actual).
 * 3) Data key: **chat** (String). *Sin agregación* en la ventana de tiempo.
 ***************************************************************************/

self.onInit = function () {
  const s = self.ctx.$scope;
  s.content = '';
  s.chatlist = [];

  // Utilidad: scroll al final
  function scrollBottom() {
    const cont = self.ctx.$container && self.ctx.$container[0].querySelector('.chat-list');
    if (cont) cont.scrollTop = cont.scrollHeight;
  }

  // Enviar mensaje -> guardar como telemetría de USER
  s.sendMsg = function () {
    const text = (s.content || '').trim();
    if (!text) return;
    if (text.length > 1000) { console.warn('Mensaje muy largo (>1000).'); return; }

    const cu = self.ctx.currentUser || {};
    const userId = (cu.userId && (cu.userId.id || cu.userId)) || null;
    if (!userId) { console.error('userId no disponible'); return; }

    const nick = [cu.firstName || '', cu.lastName || ''].join(' ').trim() || cu.name || cu.email || 'Yo';
    const ts = Date.now();

    // Estructura de mensaje (guardamos como string JSON en la key "chat")
    const msg = { role: 'user', userId: userId, nick: nick, content: text, ts: ts };
    const body = [{ ts: ts, values: { chat: JSON.stringify(msg) } }];

    self.ctx.http.post(`/api/plugins/telemetry/USER/${userId}/timeseries/ANY`,
      body,
      {
        headers: {
          'content-type': 'application/json',
          'X-Authorization': 'Bearer ' + localStorage.getItem('jwt_token')
        }
      }
    ).subscribe(
      () => {
        // Mostrarlo al toque (optimista). onDataUpdated volverá a ordenar todo.
        s.content = '';
        s.chatlist.push(msg);
        self.ctx.detectChanges();
        setTimeout(scrollBottom, 0);
      },
      (err) => {
        console.error('Error enviando chat:', err);
      }
    );
  };

  // Enter = enviar ; Shift+Enter = nueva línea
  s.onKeyDown = function (ev) {
    if (ev.key === 'Enter' && !ev.shiftKey) {
      ev.preventDefault();
      s.sendMsg();
    }
  };
};

self.onDataUpdated = function () {
  const s = self.ctx.$scope;
  const rows = [];

  // Tomamos todo el histórico visible en la ventana de tiempo
  const series = (self.ctx.data && self.ctx.data[0] && self.ctx.data[0].data) ? self.ctx.data[0].data : [];
  for (let i = 0; i < series.length; i++) {
    const ts = series[i][0];
    let val = series[i][1];

    let msg = null;
    if (typeof val === 'string') {
      // Guardamos como string JSON; lo parseamos
      try { msg = JSON.parse(val); }
      catch { msg = { role: 'system', content: String(val) }; }
    } else if (val && typeof val === 'object') {
      // (por si llega como objeto)
      msg = val;
    }

    if (msg) {
      msg.ts = msg.ts || ts;
      // Normalizamos role por si no viene
      if (!msg.role) {
        // Si userId coincide con el actual, lo tratamos como "user", sino "assistant"
        const cu = self.ctx.currentUser || {};
        const myId = (cu.userId && (cu.userId.id || cu.userId)) || null;
        msg.role = (myId && msg.userId == myId) ? 'user' : 'assistant';
      }
      rows.push(msg);
    }
  }

  // Orden por tiempo ascendente
  rows.sort((a, b) => (a.ts || 0) - (b.ts || 0));
  s.chatlist = rows;
  self.ctx.detectChanges();

  // Scroll al final luego de render
  setTimeout(() => {
    const cont = self.ctx.$container && self.ctx.$container[0].querySelector('.chat-list');
    if (cont) cont.scrollTop = cont.scrollHeight;
  }, 10);
};

self.typeParameters = function () {
  // Igual que tu ejemplo: un datasource / una key
  return { maxDatasources: 1, maxDataKeys: 1, minDataKeys: 1 };
};

