descriptor.templateCss:
.industrial
{
    top: 50%;
     transform: translate(-50%, -50%);
    position: absolute;
  left: 50%;
}

descriptor.controllerScript:
self.onInit = function() {
    var settings = self.ctx.settings;

    $('.industrial', self.ctx.$container).each(function() {
                $(this).industrial({
                    default_value: true,
                    low: settings.minValue, 
                    high: settings.maxValue,
                    tick_scale: true, 
                    tick_scale_frequency: 1,
                    tick_amount: settings.numberOfTicks,
                    tick_height: 2,
                    digit_amount: 4   
            });
    });
}

self.onDataUpdated = function() {
    var settings = self.ctx.settings;
    var value = 0;

    if (self.ctx.defaultSubscription.data.length > 0)
    {

        if (self.ctx.defaultSubscription.data[0].data.length > 0)
        value = self.ctx.defaultSubscription.data[0].data[0][1];


        }


  $('.industrial', self.ctx.$container).each(function() {
            $(this).industrial(parseFloat(value));
  });


    function isNumber(n) {
        return !isNaN(parseFloat(n)) && isFinite(n);
    }
}

self.onResize = function() {

}

self.onDestroy = function() {
}
