descriptor.controllerScript:
self.onInit = function() {
    var settings = self.ctx.settings;
    var scope = self.ctx.$scope;


    $('#myImage', self.ctx.$container).click(function(e){
        if ( $('#myImage', self.ctx.$container)[0].src.includes("bulbon"))
        {
        $('#myImage', self.ctx.$container)[0].src =  "//sre-solutions.com/static/tb-widget/pic_bulboff.gif"
        requestChange(0,false);
        }
        else
    {
        $('#myImage', self.ctx.$container)[0].src =  "//sre-solutions.com/static/tb-widget/pic_bulbon.gif"
        requestChange(0,true);
    }
    });

    function requestChange(g, value)
    {

        var method = settings.changeRequestMethod;

        var paramsBody = value;


        var $injector = self.ctx.$injector;
        let deviceService =self.ctx.deviceService;

        if (self.ctx.datasources.length > 0)
        {
        var dataKeyCounter = 0;
        var entityId = self.ctx.datasources[0].entityId

            var requestBody = {
                 method: method,
                 params: paramsBody

            }

       console.log(deviceService);


        deviceService.sendOneWayRpcCommand(entityId, requestBody).subscribe((responseBody) => {});
        }
    }




}

self.onDataUpdated = function() {


 console.log (self.ctx);
    var settings = self.ctx.settings;
    var value = 0;

    if (self.ctx.defaultSubscription.data.length > 0)
    {

            if (self.ctx.defaultSubscription.data.length > 0)
            {
                if (self.ctx.defaultSubscription.data[0].data.length > 0)
                    value = parseInt(self.ctx.defaultSubscription.data[0].data[0][1]);

                if (value > 0)
                {
                    $('#myImage', self.ctx.$container)[0].src =  "//sre-solutions.com/static/tb-widget/pic_bulbon.gif"
                }
                else
                {
                    $('#myImage', self.ctx.$container)[0].src =  "//sre-solutions.com/static/tb-widget/pic_bulboff.gif"
                }

            }




    }

    function isNumber(n) {
        return !isNaN(parseFloat(n)) && isFinite(n);
    }
}

self.onResize = function() {
    if (self.ctx.width < parseInt(self.ctx.height) + 50 + "px")
    {
 $('#myImage', self.ctx.$container)[0].style.width = self.ctx.width + "px";
 $('#myImage', self.ctx.$container)[0].style.height = "auto";
    }
 else
     {
 $('#myImage', self.ctx.$container)[0].style.width = "auto";
 $('#myImage', self.ctx.$container)[0].style.height = self.ctx.height + "px";
    }
}

self.onDestroy = function() {
}

