descriptor.templateCss:
#slider {

    top: 50%;
     transform: translate(-50%, -50%);
    position: absolute;
    left: 50%;



}

descriptor.controllerScript:
//https://roundsliderui.com/
var namespace;
var cssParser = new cssjs();

self.onInit = function() {

var settings = self.ctx.settings;

 $('#slider', self.ctx.$container).roundSlider({
    sliderType: "min-range",
    circleShape: "pie",
    startAngle: "315",
    lineCap: "round",
    radius: 130,
    width: 20,
    step: settings.sliderStepWidth,
    min: settings.sliderMinValue,
    max: settings.sliderMaxValue,

    svgMode: true,
	  pathColor: "#eee",
	  borderWidth: 0,

	  startValue: 0,

    valueChange: function (e) {
    	var color = e.isInvertedRange ? settings.sliderbgColorPositive : settings.sliderbgColorNegative;

      $('#slider', self.ctx.$container).roundSlider({ "rangeColor": color, "tooltipColor": color });


      var method = settings.sliderChangeRequestMethod;

        var paramsBody = $('#slider', self.ctx.$container).data("roundSlider").getValue();



        self.ctx.controlApi.sendTwoWayCommand(method, 
                                    paramsBody, settings.requestTimeout)
                    .subscribe(
                        function success(responseBody) {

                        }
                    );

    }
});

var sliderObj =$('#slider', self.ctx.$container).data("roundSlider");
requestStatus();

    function requestStatus() {
    method = settings.sliderStatusRequestMethod;
    paramsBody = "";
    self.ctx.controlApi.sendTwoWayCommand(method, 
                        paramsBody, 
                        settings.requestTimeout)
        .subscribe(
            function success(responseBody) {
                   sliderObj.setValue(parseFloat(responseBody));   

                }
                );
            }
}


self.onResize = function() {

	var radius = self.ctx.width / 2 - 10;
	if (self.ctx.width > self.ctx.height)
	    radius = self.ctx.height / 2 - 10;
  var width = 14;
  // here you can restrict the radius and width
  // based on your application UI structure
  if (radius > 200) radius = 200;
  if (radius > 120) width = 22;
  else if (radius > 70) width = 18;
  $('#slider', self.ctx.$container).roundSlider({ "radius": radius, "width": width })




}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {

            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 500
            },
            "sliderbgColorPositive": {
                "title": "Slider Background Color Positive",
                "type": "string",
                "default": "#4CAF50"
            },
            "sliderbgColorNegative": {
                "title": "Slider Background Color Negative",
                "type": "string",
                "default": "#4CAF50"
            },
            "sliderMinValue": {
                "title": "Min. Value",
                "type": "number",
                "default": 1
            },
            "sliderMaxValue": {
                "title": "Max. Value",
                "type": "number",
                "default": 100
            },
            "sliderStepWidth": {
                "title": "Step Width",
                "type": "number",
                "default": 1
            },
            "sliderChangeRequestMethod": {
                "title": "Change request Method",
                "type": "string",
                "default": "setValue"
            },
            "sliderStatusRequestMethod": {
                "title": "Status request Method",
                "type": "string",
                "default": "getValue"
            }
        },
        "required": [
                     "requestTimeout",
                     "sliderbgColorPositive",
                     "sliderbgColorNegative",
                     "sliderMinValue",
                     "sliderMaxValue",
                     "sliderChangeRequestMethod",
                     "sliderStatusRequestMethod"
                     ]
    },
    "form": [
        "requestTimeout",
        {
            "key": "sliderbgColorPositive",
            "type": "color"
        },{
            "key": "sliderbgColorNegative",
            "type": "color"
        },
        "sliderMinValue",
        "sliderMaxValue",
        "sliderStepWidth",
        "sliderChangeRequestMethod",
        "sliderStatusRequestMethod"

    ]
}
