descriptor.templateCss:
:root {
  --bg-me: #DCF8C6;            /* verde claro tipo WhatsApp */
  --fg-me: #111;
  --bg-bot: #2a2f3a;           /* gris oscuro */
  --fg-bot: #fff;
  --border: rgba(0,0,0,.12);
}

.chat-container{
  width:100%; height:100%;
  display:flex; flex-direction:column;
}

.chat-list{
  flex:1; overflow:auto; padding:12px;
  display:flex; flex-direction:column; gap:8px;
}

.chat-row{ display:flex; }
.chat-row.me{ justify-content:flex-end; }
.chat-row.bot{ justify-content:flex-start; }

.bubble{
  max-width:75%;
  padding:10px 12px; border-radius:14px;
  box-shadow: 0 1px 2px rgba(0,0,0,.08);
}
.chat-row.me .bubble{
  background:var(--bg-me); color:var(--fg-me);
  border-top-right-radius:4px;
}
.chat-row.bot .bubble{
  background:var(--bg-bot); color:var(--fg-bot);
  border-top-left-radius:4px;
}

.meta{
  display:flex; gap:8px; align-items:center;
  font-size:12px; opacity:.8; margin-bottom:4px;
}
.nick{ font-weight:600; }
.text{ white-space:pre-wrap; word-break:break-word; }

.chat-input{
  display:flex; gap:8px; align-items:flex-end;
  padding:10px; border-top:1px solid var(--border);
}
.chat-input textarea{
  flex:1; resize:none; min-height:40px; max-height:140px;
  padding:10px 12px; border-radius:10px; outline:none;
  border:1px solid var(--border); background:transparent;
  color:inherit; font:inherit;
}
.chat-input .send{
  padding:10px 14px; border-radius:10px;
  border:1px solid var(--border);
  background:rgba(0,0,0,.06); color:inherit; cursor:pointer;
}
.chat-input .send:hover{ filter:brightness(1.1); }

descriptor.controllerScript:
/****************************************************************************
 * Chat persistente (USER → TS key: "chat")
 * - Carga historial SOLO al abrir el dashboard (una vez)
 * - Luego NO hace polling; escucha actualizaciones del datasource en vivo
 ***************************************************************************/

self.onInit = function () {
  const s = self.ctx.$scope;

  // ======= Config =======
  const KEY = 'chat';                        // clave de timeseries del chat
  const LOOKBACK_MS = 1000 * 60 * 60 * 24 * 30; // 30 días de historial
  const HISTORY_LIMIT = 1000;                // tope de mensajes que mantenemos

  s.content = '';
  s.chatlist = [];
  let userId = null;
  let lastTs = 0; // último timestamp renderizado

  // ======= Utilidades =======
  function token() { return localStorage.getItem('jwt_token'); }
  function getUserId() {
    const cu = self.ctx.currentUser || {};
    return (cu?.userId?.id || cu?.userId) || null;
  }
  function scrollBottom() {
    const cont = self.ctx.$container && self.ctx.$container[0].querySelector('.chat-list');
    if (cont) cont.scrollTop = cont.scrollHeight;
  }
  function parseMsg(ts, val) {
    let msg = null;
    if (typeof val === 'string') {
      try { msg = JSON.parse(val); } catch { msg = { role: 'system', content: String(val) }; }
    } else if (val && typeof val === 'object') {
      msg = val;
    }
    if (!msg) return null;
    msg.ts = msg.ts || ts;
    if (!msg.role) {
      msg.role = (msg.userId && userId && msg.userId == userId) ? 'user' : 'assistant';
    }
    return msg;
  }
  function mergeAndRender(newMsgs) {
    if (!newMsgs || !newMsgs.length) return;
    const keyOf = (m) => `${m.ts}|${m.role}|${m.content}`;
    const map = new Map();
    [...s.chatlist, ...newMsgs].forEach(m => map.set(keyOf(m), m));
    s.chatlist = Array.from(map.values()).sort((a,b) => (a.ts||0)-(b.ts||0)).slice(-HISTORY_LIMIT);
    lastTs = s.chatlist.length ? (s.chatlist[s.chatlist.length-1].ts||0) : 0;
    self.ctx.detectChanges();
    setTimeout(scrollBottom, 0);
  }

  // ======= REST: traer historial una sola vez (al abrir) =======
  function fetchInitialHistory() {
    const end = Date.now();
    const start = end - LOOKBACK_MS;
    const url = `/api/plugins/telemetry/USER/${userId}/values/timeseries` +
                `?keys=${encodeURIComponent(KEY)}&startTs=${start}&endTs=${end}` +
                `&limit=${HISTORY_LIMIT}&agg=NONE`;
    return self.ctx.http.get(url, {
      headers: { 'X-Authorization': 'Bearer ' + token() }
    }).subscribe(
      (resp) => {
        const arr = (resp && resp[KEY]) ? resp[KEY] : [];
        const msgs = [];
        for (let i=0;i<arr.length;i++) {
          const m = parseMsg(arr[i].ts, arr[i].value);
          if (m) msgs.push(m);
        }
        mergeAndRender(msgs);
      },
      (e) => console.error('Error cargando historial inicial:', e)
    );
  }

  // ======= Enviar mensaje: guarda telemetría (sin esperar a REST) =======
  s.sendMsg = function () {
    const text = (s.content || '').trim();
    if (!text) return;
    if (text.length > 2000) { console.warn('Mensaje muy largo (>2000).'); return; }

    const cu = self.ctx.currentUser || {};
    const nick = [cu.firstName || '', cu.lastName || ''].join(' ').trim() || cu.name || cu.email || 'Yo';
    const ts = Date.now();
    const msg = { role: 'user', userId, nick, content: text, ts };
    const body = [{ ts, values: { [KEY]: JSON.stringify(msg) } }];

    self.ctx.http.post(
      `/api/plugins/telemetry/USER/${userId}/timeseries/ANY`,
      body,
      { headers: { 'content-type': 'application/json', 'X-Authorization': 'Bearer ' + token() } }
    ).subscribe(
      () => {
        s.content = '';
        // Optimista: lo muestro ya. El datasource en vivo también lo traerá.
        mergeAndRender([msg]);
      },
      (err) => console.error('Error enviando chat:', err)
    );
  };

  // Enter = enviar ; Shift+Enter = salto de línea
  s.onKeyDown = function (ev) {
    if (ev.key === 'Enter' && !ev.shiftKey) {
      ev.preventDefault();
      s.sendMsg();
    }
  };

  // ======= Boot =======
  userId = getUserId();
  if (!userId) { console.error('userId no disponible'); return; }
  fetchInitialHistory(); // <-- SOLO una vez al abrir
};

// IMPORTANTE: acá NO pedimos historial otra vez.
// Solo integramos lo que llegue del datasource (realtime) y NO borramos si viene vacío.
self.onDataUpdated = function () {
  const s = self.ctx.$scope;
  const ds = (self.ctx.data && self.ctx.data[0] && self.ctx.data[0].data) ? self.ctx.data[0].data : [];
  if (!ds.length) return; // no resetear si no hay puntos en esta ventana

  const rows = [];
  for (let i = 0; i < ds.length; i++) {
    const ts = ds[i][0];
    const val = ds[i][1];
    const m = (function(){
      if (typeof val === 'string') { try { return JSON.parse(val); } catch { return { role:'system', content:String(val) }; } }
      if (val && typeof val === 'object') return val;
      return null;
    })();
    if (m) { m.ts = m.ts || ts; rows.push(m); }
  }
  // Podrían venir también mensajes ya cargados: hacemos merge + dedupe
  mergeAndRender(rows);

  function mergeAndRender(list) {
    const keyOf = (m) => `${m.ts}|${m.role}|${m.content}`;
    const map = new Map();
    [...(s.chatlist||[]), ...list].forEach(m => map.set(keyOf(m), m));
    s.chatlist = Array.from(map.values()).sort((a,b)=> (a.ts||0)-(b.ts||0)).slice(-1000);
    self.ctx.detectChanges();
    setTimeout(() => {
      const cont = self.ctx.$container && self.ctx.$container[0].querySelector('.chat-list');
      if (cont) cont.scrollTop = cont.scrollHeight;
    }, 0);
  }
};

self.typeParameters = function () {
  return { maxDatasources: 1, maxDataKeys: 1, minDataKeys: 1 };
};

